import React, {Component} from 'react'
import './App.css'
import Navbar from './Navbar.js'
import Web3 from 'web3'

class App extends Component {
    async Web3() {
        if(window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        }
        else if(window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
        }
        else {
            window.alert('No Ethereum browser detected! You should check out MetaMask!')
        }
    }
    
    constructor(props) {
        super(props)
        this.state = {
            account: '0x0'
        }
    }

    // Our React Code
    render() {
        return (
            <div>
                <Navbar account={this.state.account}/>
                <div className='text-center' style={{color:'green', fontSize:'30px'}}>
                    <h>Hello World!</h>
                </div>            
            </div>
        )
    }
}

export default App;

// HTML - The markup language for writing basic text and websites
// CSS - Styles the website (colors, fonts, sizes)
// Javascript - Allows the websites to be dynamic
